#import "StockQuoteExample.h"
#import "StockQuote.h"

@implementation StockQuoteExample

- (void)run
{
  // Create the service
  StockQuote *service = [StockQuote service];
  service.logging = YES;
  // service.username = @"username";
  // service.password = @"password";

  // Returns NSString *Get Stock quote for a company Symbol
  [service GetQuote:self action:@selector(GetQuoteHandler:) symbol:@""];
  
}

// Handle the response from GetQuote
- (void)GetQuoteHandler:(id)value
{
    // Handle errors
    if ([value isKindOfClass:[NSError class]]) {
        NSLog(@"%@", value);
        return;
    }

    // Handle faults
    if ([value isKindOfClass:[SoapFault class]]) {
        NSLog(@"%@", value);
        return;
    }
      
    // Do something with the NSString * result
    NSString * result = (NSString *)value;
    NSLog(@"GetQuote returned the value: %@", result);
}
  
@end
